select.where:
  
           ${filterSql}
             and a.release_device_id = #{releaseDeviceId}
              and a.schedule_id = #{scheduleId}
              and a.schedule_id in 
             (SELECT za.ad_schedule_id FROM zx_ad_schedule za WHERE za.schedule_name LIKE concat('%', #{scheduleName}, '%'))
              and a.sx_schedule_id = #{sxScheduleId}
              and a.device_id = #{deviceId}
              and a.device_id in
             (SELECT zd.device_id FROM zx_device zd WHERE zd.device_sn LIKE concat('%', #{deviceSn}, '%'))
              and a.down_time = #{downTime}
              and a.play_time = #{playTime}
             
				 AND date_format(a.play_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
			 
			 
				 AND date_format(a.play_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
			 
              and a.price = #{price}
              and a.create_by = #{createBy}
              and a.create_time = #{createTime}
              and a.update_by = #{updateBy}
              and a.update_time = #{updateTime}

mapper.update:
update zx_ad_release_record
schedule_id = #{scheduleId}, sx_schedule_id = #{sxScheduleId}, device_id = #{deviceId}, down_time = #{downTime}, play_time = #{playTime}, price = #{price}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where release_device_id = #{releaseDeviceId}

