mapper.select:
        
          
            ${filterSql}
             and zas.ad_schedule_id = #{adScheduleId}
             
              	and zas.schedule_name like concat('%', #{scheduleName}, '%')
             
              and zas.release_type = #{releaseType}
              and zas.release_position = #{releasePosition}
              and zas.sx_schedule_id = #{sxScheduleId}
              and zas.pid = #{pId}
              and zas.tid = #{tId}
              and zas.theme_template_id = #{themeTemplateId}
              and zas.status = #{status}
              and zas.total_pay = #{totalPay}
              and zas.prepay = #{prepay}
              and zas.release_term_num = #{releaseTermNum}
              and zas.advertiser = #{advertiser}
              and zas.promotioner = #{promotioner}
              and zas.releaser = #{releaser}
              and zas.approved = #{approved}
              and zas.approved_remark = #{approvedRemark}
              and zas.approved_user = #{approvedUser}
              and zas.is_del = #{isDel}
              and zas.release_note = #{releaseNote}
              and zas.create_by = #{createBy}
             
				 AND date_format(zas.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
			 
			 
				 AND date_format(zas.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
			 
              and zas.update_by = #{updateBy}
              and zas.update_time = #{updateTime}
              and zas.total_time = #{totalTime}
              and zas.pay_status = #{payStatus}
              and zas.qr_url = #{qrUrl}
              and zas.ad_schedule_id in 
             	(select distinct zrd.schedule_id from zx_release_device zrd where zrd.device_id in
             	(select distinct zd.device_id from zx_device zd where zd.device_code like concat('%',#{deviceCode},'%'))) 
             
             and zas.is_del='0'
         
         order by zas.ad_schedule_id desc

mapper.update:
update zx_ad_schedule
schedule_name = #{scheduleName}, release_type = #{releaseType}, release_position = #{releasePosition}, release_days = #{releaseDays}, sx_schedule_id = #{sxScheduleId}, pid = #{pId}, tid = #{tId}, theme_template_id = #{themeTemplateId}, status = #{status}, total_pay = #{totalPay}, prepay = #{prepay}, release_term_num = #{releaseTermNum}, advertiser = #{advertiser}, promotioner = #{promotioner}, releaser = #{releaser}, approved = #{approved}, approved_remark = #{approvedRemark}, approved_user = #{approvedUser}, is_del = #{isDel}, release_note = #{releaseNote}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, total_time = #{totalTime}, pay_status = #{payStatus}, release_status = #{releaseStatus}, qr_url = #{qrUrl}, dead_line = #{deadLine}, ext_schedule_id=#{extScheduleId},
where ad_schedule_id = #{adScheduleId}

