             	(select distinct zrd.schedule_id from zx_release_device zrd where zrd.device_id in
             	(select distinct zd.device_id from zx_device zd where zd.device_code like concat('%',#{deviceCode},'%'))) 
             
             and zas.is_del='0'
         
         order by zas.ad_schedule_id desc
