mapper.select:
        
          
            ${filterSql}
             and place_id = #{placeId}
              and parent_place_id = #{parentPlaceId}
              and place_code = #{placeCode}
              and place_type = #{placeType}
              and scene = #{scene}
             
             	 and name like concat('%', #{name}, '%')
             
              and sname = #{sname}
              and address = #{address}
              and province = #{province}
              and city = #{city}
              and county = #{county}
              and lon = #{lon}
              and lat = #{lat}
              and packet = #{packet}
              and tissue_price = #{tissuePrice}
              and tissue_len = #{tissueLen}
              and service_point = #{servicePoint}
              and repair_id = #{repairId}
              and supply_id = #{supplyId}
              and operator_id = #{operatorId}
              and owner_id = #{ownerId}
              and device_count = #{deviceCount}
              and ct_bank = #{ctBank}
              and ct_account = #{ctAccount}
              and ct_receiver = #{ctReceiver}
              and ct_phone = #{ctPhone}
              and ct_identity = #{ctIdentity}
              and ct_rate = #{ctRate}
              and status = #{status}
              and del_flag = #{delFlag}
              and note = #{note}
              and create_by = #{createBy}
             
				AND date_format(p.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
			 
			 
				AND date_format(p.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
			 
              and update_by = #{updateBy}
              and update_time = #{updateTime}
             
				AND (
				#{params.person} like CONCAT('%',',',p.repair_id,',','%')
				OR 
				#{params.person} like CONCAT('%',',',p.supply_id,',','%')
				OR
				#{params.person} like CONCAT('%',',',p.operator_id,',','%')
				OR
				#{params.person} like CONCAT('%',',',p.owner_id,',','%')
				)
			 
			    
				AND #{params.city} like CONCAT('%',',',p.city,',','%')
			 
			    
				AND #{params.county} like CONCAT('%',',',p.county,',','%')
			 
         
         ORDER BY p.create_time desc

mapper.update:
update zx_place
parent_place_id = #{parentPlaceId}, place_code = #{placeCode}, place_type = #{placeType}, scene = #{scene}, name = #{name}, sname = #{sname}, address = #{address}, province = #{province}, city = #{city}, county = #{county}, lon = #{lon}, lat = #{lat}, packet = #{packet}, tissue_price = #{tissuePrice}, tissue_len = #{tissueLen}, service_point = #{servicePoint}, repair_id = #{repairId}, supply_id = #{supplyId}, operator_id = #{operatorId}, owner_id = #{ownerId}, device_count = #{deviceCount}, ct_bank = #{ctBank}, ct_account = #{ctAccount}, ct_receiver = #{ctReceiver}, ct_phone = #{ctPhone}, ct_identity = #{ctIdentity}, ct_rate = #{ctRate}, status = #{status}, del_flag = #{delFlag}, note = #{note}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where place_id = #{placeId}

