select.where:
${filterSql}
    
     and a.agent_id = #{agentId}
      and a.pagent_id = #{pagentId}
      and a.agent_name like concat('%', #{agentName}, '%')
      and a.province = #{province}
      and a.city = #{city}
      and a.county = #{county}
      and a.level = #{level}
      and a.device_num = #{deviceNum}
     and a.del_flag != '2'
      and a.status = #{status}
      and a.manager_id = #{managerId}
      and a.manager_name like concat('%', #{managerName}, '%')
      and a.manager_phone like concat('%', #{managerPhone}, '%')
      and a.bank_receiver = #{bankReceiver}
      and a.bank_account = #{bankAccount}
      and a.bank_name = #{bankName}
      and a.promotor_id = #{promotorId}
      and a.ad_rate = #{adRate}
      and a.scan_rate = #{scanRate}
      and a.promotion_rate = #{promotionRate}
      and a.create_by = #{createBy}
      and a.create_time = #{createTime}
      and a.update_by = #{updateBy}
      and a.update_time = #{updateTime}

mapper.update:
update zx_agent
pagent_id = #{pagentId}, agent_name = #{agentName}, province = #{province}, city = #{city}, county = #{county}, level = #{level}, device_num = #{deviceNum}, del_flag = #{delFlag}, status = #{status}, buyer_id = #{buyerId}, puser_id = #{puserId}, user_type = #{userType}, manager_id = #{managerId}, manager_name = #{managerName}, manager_phone = #{managerPhone}, bank_receiver = #{bankReceiver}, bank_account = #{bankAccount}, bank_name = #{bankName}, promotor_id = #{promotorId}, ad_rate = #{adRate}, ad_carousel_rate = #{adCarouselRate}, scan_rate = #{scanRate}, prom_direct_rate = #{promDirectRate}, prom_indirect_rate = #{promIndirectRate}, prom_paper_rate = #{promPaperRate}, promotion_rate = #{promotionRate}, direct_agent_rate = #{directAgentRate}, serve_rate = #{serveRate}, agency_fee = #{agencyFee}, subsidy_rate = #{subsidyRate}, update_by = #{updateBy}, update_time = #{updateTime},
where agent_id = #{agentId}

