select.where:

        ${filterSql}
           and a.join_id = #{joinId}
            and a.joiner_name like concat('%', #{joinerName}, '%')
            and a.joiner_id = #{joinerId}
           and a.manager_name like concat('%', #{managerName}, '%')
           and b.phonenumber like concat('%', #{managerPhone}, '%')
            and a.device_num = #{deviceNum}
            and a.bank_account = #{bankAccount}
            and a.bank_receiver = #{bankReceiver}
            and a.bank_name = #{bankName}
            and a.status = #{status}
           and a.del_flag != '2'
            and a.promotion_rate = #{promotionRate}
            and a.ad_rate = #{adRate}
            and a.scan_rate = #{scanRate}
            and a.create_by = #{createBy}
            and a.create_time = #{createTime}
            and a.update_by = #{updateBy}
            and a.update_time = #{updateTime}

mapper.update:
update zx_join
joiner_name = #{joinerName}, joiner_id = #{joinerId}, manager_name = #{managerName}, manager_phone = #{managerPhone}, device_num = #{deviceNum}, bank_account = #{bankAccount}, bank_receiver = #{bankReceiver}, bank_name = #{bankName}, status = #{status}, buyer_id = #{buyerId}, puser_id = #{puserId}, user_type = #{userType}, ad_rate = #{adRate}, ad_carousel_rate = #{adCarouselRate}, scan_rate = #{scanRate}, prom_direct_rate = #{promDirectRate}, prom_indirect_rate = #{promIndirectRate}, prom_paper_rate = #{promPaperRate}, promotion_rate = #{promotionRate}, serve_rate = #{serveRate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where join_id = #{joinId}

update zx_join
balance = #{balance}, frozen_balance = #{frozenBalance}, update_time = now()
where join_id = #{joinId}

update zx_join
ad_rate = #{adRate}, ad_carousel_rate = #{adCarouselRate}, scan_rate = #{scanRate}, prom_direct_rate = #{promDirectRate}, prom_indirect_rate = #{promIndirectRate}, prom_paper_rate = #{promPaperRate}, promotion_rate = #{promotionRate}, serve_rate = #{serveRate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where join_id = #{joinId}
