select.where:

       ${filterSql}
           and a.repair_id = #{repairId}
            and a.repair_name like concat('%', #{repairName}, '%')
            and a.manager_name = #{managerName}
            and a.manager_id = #{managerId}
           and b.phonenumber like concat('%', #{managerPhone}, '%')
            and a.bank_receiver = #{bankReceiver}
            and a.bank_account = #{bankAccount}
            and a.bank_name = #{bankName}
            and a.promotion_rate = #{promotionRate}
            and a.ad_rate = #{adRate}
            and a.scan_rate = #{scanRate}
            and a.status = #{status}
           and a.del_flag != '2'
            and a.device_num = #{deviceNum}
            and a.create_by = #{createBy}
            and a.create_time = #{createTime}
            and a.update_by = #{updateBy}
            and a.update_time = #{updateTime}

mapper.update:
update zx_repair
repair_name = #{repairName}, manager_name = #{managerName}, manager_id = #{managerId}, manager_phone = #{managerPhone}, bank_receiver = #{bankReceiver}, bank_account = #{bankAccount}, bank_name = #{bankName}, ad_rate = #{adRate}, ad_carousel_rate = #{adCarouselRate}, scan_rate = #{scanRate}, prom_direct_rate = #{promDirectRate}, prom_indirect_rate = #{promIndirectRate}, prom_paper_rate = #{promPaperRate}, promotion_rate = #{promotionRate}, subsidy_rate = #{subsidyRate}, status = #{status}, buyer_id = #{buyerId}, puser_id = #{puserId}, user_type = #{userType}, device_num = #{deviceNum}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where repair_id = #{repairId}

update zx_repair
balance = #{balance}, frozen_balance = #{frozenBalance}, update_time = now()
where repair_id = #{repairId}
