mapper.select:
select d.device_order_id, 
d.device_id, 
v.device_code as deviceCode, 
d.terminal_id,
t.terminal_code as terminalCode,
 d.promotioner_id,
 (select u.user_name from sys_user u where u.user_id = d.promotioner_id) as promotionerName,
  d.price, d.cut_off, 
  d.seller_id, 
  (select u.user_name from sys_user u where u.user_id = d.seller_id) as sellerName, 
  d.status, d.buyer_id, d.buyer_name, 
d.buyer_open_id, d.create_by, d.create_time, d.update_by, d.update_time from zx_device_order d 
left join zx_device v on v.device_id = d.device_id 
left join zx_terminal t on t.terminal_id = d.terminal_id
  
     and device_order_id = #{deviceOrderId}
      and device_id = #{deviceId}
      and v.device_code like concat('%', #{deviceCode}, '%')
      and terminal_id = #{terminalId}
      and t.terminal_code  like concat('%', #{terminalCode}, '%')
      and promotioner_id = #{promotionerId}
      and price = #{price}
      and cut_off = #{cutOff}
      and seller_id = #{sellerId}
      and status = #{status}
      and buyer_id = #{buyerId}
      and buyer_name = #{buyerName}
      and buyer_open_id = #{buyerOpenId}
      and create_by = #{createBy}
      and create_time = #{createTime}
      and update_by = #{updateBy}
      and update_time = #{updateTime}

mapper.update:
update zx_device_order
device_id = #{deviceId}, terminal_id = #{terminalId}, promotioner_id = #{promotionerId}, price = #{price}, cut_off = #{cutOff}, seller_id = #{sellerId}, status = #{status}, buyer_id = #{buyerId}, buyer_name = #{buyerName}, buyer_open_id = #{buyerOpenId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where device_order_id = #{deviceOrderId}
