mapper.select:
        
          
            
			(
			  date_format(sum_date, '%Y年%m月') =#{params.onlineTime} 
			 date_format(sum_date, '%Y-%m') = date_format(
					date_sub(curdate(), INTERVAL 1 DAY),
					'%Y-%m'
				)
				
			)
             and device_online_id = #{deviceOnlineId}
              and sum_date = #{sumDate}
              and device_id = #{deviceId}
              and device_code = #{deviceCode}
              and terminal_id = #{terminalId}
              and address = #{address}
              and release_address = #{releaseAddress}
              and agent_id = #{agentId}
              and join_id = #{joinId}
              and repair_id = #{repairId}
              and shopper_id = #{shopperId}
              and online_time = #{onlineTime}
              and create_time = #{createTime}
              and update_time = #{updateTime}
             
         
         order by sum_date desc


  
     and device_online_id = #{deviceOnlineId}
      and sum_date = #{sumDate}
      and device_id = #{deviceId}
      and device_code = #{deviceCode}
      and terminal_id = #{terminalId}
      and address = #{address}
      and release_address = #{releaseAddress}
      and agent_id = #{agentId}
      and join_id = #{joinId}
      and repair_id = #{repairId}
      and shopper_id = #{shopperId}
      and online_time = #{onlineTime}
      and create_time = #{createTime}
      and update_time = #{updateTime}
 
 limit 1

mapper.update:
update zx_online_device
sum_date = #{sumDate}, device_id = #{deviceId}, device_code = #{deviceCode}, terminal_id = #{terminalId}, address = #{address}, release_address = #{releaseAddress}, agent_id = #{agentId}, join_id = #{joinId}, repair_id = #{repairId}, shopper_id = #{shopperId}, online_time = #{onlineTime}, create_time = #{createTime}, update_time = #{updateTime},
where device_online_id = #{deviceOnlineId}
